import React from 'react'
import articles from './article-content'

export default function ArticleList() {
    return (
        <>
            <h1>Articles</h1>
            {articles.map(article => (
                <div>
                    <h3>{article.title}</h3>
                    <p>{article.content[0].substring(0, 150)} ... </p>
                </div>
            ))}
        </>
    )
}
