import React from 'react'

export default function CommentsList({ comments }) {
    return (
        <>
            <h3>Comments:</h3>
            {comments.map((comment, i) => (
                <div className="comment" key={i}>
                    <h4>{comment.postedBy}</h4>
                    <p>{comment.text}</p>
                </div>
            ))}
        </>
    )
}
